import React, { Component } from 'react';
import StarRatingComponent from 'react-star-rating-component';

class ReviewWrite extends Component {
	constructor() {
		super();
		this.state = {
			rating: 0,
			cleanliness: 5,
			communication: 5,
			comment: ''
		};
	}

	onStarClick(nextValue, prevValue, name) {
		console.log(name);
		this.setState({ [name]: nextValue });
	}

	renderStarRating(rating, name) {
		const nameCapitalized =
			name.charAt(0).toUpperCase() + name.slice(1);
		return (
			<div className='rhi'>
				<StarRatingComponent
					name={name}
					starCount={5}
					starColor={'#00A699'}
					value={rating}
					onStarClick={this.onStarClick.bind(
						this
					)}
				/>
				<span>
					<p>{rating}</p>
					<p>{nameCapitalized}</p>
				</span>
			</div>
		);
	}

	render() {
		const {
			rating,
			communication,
			cleanliness,
			comment
		} = this.state;
		return (
			<div>
				<h2>Write Review</h2>
				<div>
					<div>
						{this.renderStarRating(
							communication,
							'cleanliness'
						)}
						{this.renderStarRating(
							cleanliness,
							'cleanliness'
						)}
						{this.renderStarRating(
							cleanliness,
							'cleanliness'
						)}
					</div>
				</div>
			</div>
		);
	}
}

export default ReviewWrite;
