import React, { Component } from 'react'
import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'
import './RegisterPage.css'

export class RegisterPage extends Component {
    constructor(){
		super()
		this.state = { 
			currentIndex : 0
		}
	}

	check_title_index(index){
		return index === this.state.currentIndex ? "tab_title active" : "tab_title"
	}

	check_item_index(index){
		return index === this.state.currentIndex ? "tab_item show" : "tab_item"
    }

    render() {
        let _this = this
        return (
            <div className = 'register-page'>
                <div className='login-background'>
                    <img
                        className='login-img'
                        alt='background'
                        src='https://pacificahousing.ca/wp-content/uploads/2016/11/1213-small-4965.jpg'
                    />
                </div>
                <div className = 'login-box register-box'>
                    <p className = 'login-title'>
                        Register
                    </p>

                    <div>
                        { /* 动态生成Tab导航 */ }
                        <div className="tab_title_wrap">
                            { 
                                React.Children.map( this.props.children , ( element,index ) => {
                                    return(
                                        <div onClick={ (  ) => { this.setState({ currentIndex : index }) } } className={ this.check_title_index( index ) }>{ element.props.name }</div>
                                    )
                                }) 
                            }
                        </div>
                        { /* Tab内容区域 */ }
                        <div className="tab_item_wrap">
                            {
                                React.Children.map(this.props.children,( element,index )=>{
                                    return(
                                        <div className={ this.check_item_index( index ) }>{ element }</div>
                                    )
                                })
                            }
                        </div>
                    </div>

                    {/* <Tabs defaultActiveKey='guest' className = 'register-tabs'>
                        <Tab label='guest' title='As Guest'>
                        <div>
                            <input className = 'login-input register-fn-input'
                            name='firstName'
                            type='name'
                            placeholder='First Name'/>
                            <input className = 'login-input register-fn-input'
                            name='lastName'
                            type='name'
                            placeholder='Last Name'/>
                            <input className = 'login-input register-fn-input'
                            type='email'
                            placeholder='Email Address'/>
                        </div>
                        </Tab>
                        <Tab eventKey='host' title='As Host'>

                        </Tab>
                    </Tabs> */}

                    <div>
                        <button
                            type='submit'
                            className='submitButton'
                        >
                            Register
                        </button>
                    </div>
                </div>
            </div>
        )
    }
}

export default RegisterPage
