	// 			</div>
	// 			<div>
	// 				<p>{rating}</p>
	// 			</div>
	// 		</div>
	// 	);
	// };

	onChange = e => {
		this.setState({ [e.target.name]: e.target.value });
	};

	handleKeyPress = e => {
		if (e.keyCode === 8) {
			this.setState({
				comment: this.state.comment.substring(
					0,
					this.state.comment.length - 1
				)
			});
		}
	};

	onSubmitReview = () => {
		console.log('submit review');
	};

	render() {
		const {
			communication,
			cleanliness,
			value,
			comment
		} = this.state;
		return (
			<div>
				<h2>Write Review</h2>
				<div>
					<div className='ratingContainer'>
						{/* {this.renderStarRating(
							communication,
							'communication'
						)}

						{this.renderStarRating(
							cleanliness,
							'cleanliness'
						)}
						{this.renderStarRating(
							value,
							'value'
						)} */}
						<Rating
							rating={communication}
							name={'communication'}
							onStarClick={
								this.onStarClick
							}
						/>
					</div>

					<div className='commentContainer'>
						<textarea
							className='writeComment'
							name='comment'
							value={comment}
							placeholder='Write Comment...'
							maxLength={140}
							onChange={this.onChange}
						/>
					</div>
					<div className='reviewSubmitContainer'>
						<button
							className='reviewSubmit'
							onClick={
								this
									.onSubmitReview
							}
							type='submit'
						>
							Submit
						</button>
					</div>
				</div>
			</div>
		);
	}
}

const Rating = (rating, name, onStarClick) => {
	// const nameCapitalized =
	//         name.charAt(0).toUpperCase() + name.slice(1);
	return (
		<div className='ratingComponent'>
			<div>
				<p>{name}</p>
			</div>
			<div>
				<StarRatingComponent
					name={name}
					starCount={5}
					starColor={'#00A699'}
					value={rating}
					onStarClick={onStarClick}
				/>
			</div>
			<div>
				<p>{rating}</p>
			</div>
		</div>
	);
};

export default ReviewWrite;
