import React, { Component } from 'react';
import StarRatingComponent from 'react-star-rating-component';

class ReviewWrite extends Component {
	constructor() {
		super();
		this.state = {
			rating: 5,
			communication: 5,
			cleanliness: 5,
			value: 5,
			comment: ''
		};
	}

	onStarClick(nextValue, prevValue, name) {
		this.setState({ [name]: nextValue });
		this.updateRating();
	}

	updateRating() {
		this.setState({
			rating:
				(this.state.communication +
					this.state.cleanliness +
					this.state.value) /
				3
		});
	}

	renderStarRating(rating, name) {
		const nameCapitalized =
			name.charAt(0).toUpperCase() + name.slice(1);
		return (
			<div className='rhi'>
				<StarRatingComponent
					name={name}
					starCount={5}
					starColor={'#00A699'}
					value={rating}
					onStarClick={this.onStarClick.bind(
						this
					)}
				/>
				<span>
					<p>{rating}</p>
					<p>{nameCapitalized}</p>
				</span>
			</div>
		);
	}

	render() {
		const {
			rating,
			communication,
			cleanliness,
			value,
			comment
		} = this.state;
		return (
			<div>
				<h2>Write Review</h2>
				<div>
					<div>
						{this.renderStarRating(
							communication,
							'communication'
						)}
						{this.renderStarRating(
							cleanliness,
							'cleanliness'
						)}
						{this.renderStarRating(
							value,
							'value'
						)}

						<h3>{rating}</h3>
					</div>
				</div>
			</div>
		);
	}
}

export default ReviewWrite;
