import React, { Component } from 'react';
import StarRatingComponent from 'react-star-rating-component';

class ReviewWrite extends Component {
	constructor() {
		super();
		this.state = {
			rating: 0,
			cleanliness: 5,
			comment: ''
		};
	}

	onCleanlinessClick(nextValue, prevValue, name) {
		this.setState({ cleanliness: nextValue });
	}

	renderStarRating(rating, name) {}

	render() {
		const { rating, cleanliness, comment } = this.state;
		return (
			<div>
				<h2>Write Review</h2>
				<div>
					<div>
						<div className='rhi'>
							<StarRatingComponent
								starCount={5}
								starColor={
									'#00A699'
								}
								value={
									cleanliness
								}
								onStarClick={this.onCleanlinessClick.bind(
									this
								)}
							/>
							<span>
								<p>
									{
										cleanliness
									}
								</p>
								<p>
									Cleanliness
								</p>
							</span>
						</div>
					</div>
				</div>
			</div>
		);
	}
}

export default ReviewWrite;
